import 'package:flutter/material.dart';
import 'dart:ui';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatefulWidget {
  const MyApp({super.key});

  @override
  State<MyApp> createState() => _MyAppState();
}

class _MyAppState extends State<MyApp> {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Stack(
        children: [
          const Text(
            "",
            style: TextStyle(color: Color.fromARGB(255, 52, 81, 56)),
          ),
          Center(
            child: ClipRect(
              child: BackdropFilter(
                filter: ImageFilter.blur(sigmaX: 250.0, sigmaY: 250.0),
                child: Container(
                  height: 250,
                  width: 250,
                  // decoration: const BoxDecoration(color: Colors.green),
                  alignment: Alignment.center,
                  child: const Text(
                    "Blur",
                    style: TextStyle(
                        color: Color.fromARGB(255, 0, 255, 38), fontSize: 20),
                  ),
                ),
              ),
            ),
          ),
        ],
      ),
    );
  }
}
